<template>
	<a-spin :spinning="loading">
		<a-segmented
			v-model:value="selectedCareer"
			:options="careerOptions"
			@change="careerChange"
			size="large"
			block
			style="margin: 24px 0"
		/>
		<div v-for="level in levels" :key="level" style="margin-bottom: 8px">
			<a-flex :align="`center`">
				<span v-if="level == 1" style="width: 75px">一级：</span>
				<span v-if="level == 2" style="width: 75px">二级：</span>
				<span v-if="level == 3" style="width: 75px">三级：</span>
				<span v-if="level == 4" style="width: 75px">四级：</span>
				<span v-if="level == 5" style="width: 75px">五级：</span>
				<span v-if="level == 6" style="width: 75px">六级：</span>
				<span v-if="level == 99" style="width: 75px">无等级：</span>
				<span
					v-for="item in equipmentProducts"
					:key="item.id"
					@click="selectE(item)"
				>
					<a-card
						hoverable
						style="width: 64px; margin-right: 8px"
						v-if="item.level === level"
					>
						<template #cover>
							<span
								style="
									width: 64px;
									height: 64px;
									line-height: 64px;
									border: 1px solid #d6d6d6;
									background-color: #f0f0f0;
									border-radius: 10px;
									display: inline-block;
									text-align: center;
								"
							>
								<a-image
									:src="item.image"
									:alt="item.name_zh"
									style="width: 64px"
									fallback="data:image/png;base64,"
									:preview="false"
								/>
							</span>
						</template>
					</a-card>
				</span>
			</a-flex>
		</div>
		<a-divider orientation="left" style="margin-top: 36px" v-if="!loading">
			<!-- <a-image
				:src="`${base}icon_overview.png`"
				width="36px"
				:preview="false"
			/> -->
			<a-typography-text strong>装备信息</a-typography-text>
		</a-divider>
		<a-card
		id="equipment-card"
			style=""
			:body-style="{ padding: 0, overflow: 'hidden' }"
			v-if="!loading"
		>
			<a-flex justify="space-between" :align="`center`">
				<a-card style="width: 256px; margin-right: 8px">
					<template #cover>
						<span
							style="
								width: 256px;
								height: 256px;
								line-height: 256px;
								background-color: #f0f0f0;
								display: inline-block;
								text-align: center;
							"
						>
							<a-image
								:src="
									selectedProduct?.image ||
									'data:image/png;base64,'
								"
								:alt="selectedProduct.name_zh"
								style="width: 256px"
								fallback="data:image/png;base64,"
								:preview="false"
							/>
						</span>
					</template>
				</a-card>
				<div style="padding: 12px 0; width: calc(100% - 272px)">
					<a-descriptions
						v-if="!!selectedProduct?.id"
						:title="
							selectedProduct.name_zh +
							' - ' +
							selectedProduct.name
						"
						bordered
						size="small"
					>
						<a-descriptions-item label="等级"
							>{{
								selectedProduct.level === 99
									? "-"
									: selectedProduct.level
							}}
							级</a-descriptions-item
						>
						<a-descriptions-item label="获取方式">
							{{ selectedProduct.from_desc_cn }}
						</a-descriptions-item>
						<a-descriptions-item label="合成花费">
							<span style="margin-top:5px;display: inline-block;vertical-align: middle;"><img :src="`${base}luausd.svg`" style="width: 18px;margin-right: 4px;"/></span>
							<span style="display: inline-block;vertical-align: middle;"> {{ selectedProduct.luausd }}</span>
							 
						</a-descriptions-item>
						<a-descriptions-item label="合成材料">
							<span v-if="selectedProduct.compose.length > 0">
								<div
									v-for="(
										item, index
									) in selectedProduct.compose"
									:key="index"
								>
									<a-flex>
										<span
											v-for="(i, index2) in item"
											:key="i.id"
										>
											<a-flex :align="`center`">
												<span
													style="text-align: center"
												>
													<a-tooltip>
														<template #title>{{
															maketPlaceProductData.find(
																(j) =>
																	j.id ===
																	i.id
															)?.name_zh
														}}</template>
														<span
															style="
																width: 24px;
																height: 24px;
																line-height: 24px;
																border: 1px
																	solid
																	#f0f0f0;
																background-color: rgb(
																	240,
																	240,
																	240
																);
																border-radius: 5px;
																display: inline-block;
																text-align: center;
															"
														>
															<a-image
																:src="
																	maketPlaceProductData.find(
																		(j) =>
																			j.id ===
																			i.id
																	)?.image
																"
																style="
																	width: 24px;
																"
																fallback="data:image/png;base64,"
																:preview="false"
															/>
														</span>
													</a-tooltip>
												</span>
												<span style="margin: 0 2px">
													x
												</span>
												<span>
													{{ i.amount }}
												</span>
												<span
													style="margin: 0 4px"
													v-if="
														index2 < item.length - 1
													"
												>
													+
												</span>
											</a-flex>
										</span>
									</a-flex>
								</div>
							</span>
							<span v-else> - </span>
						</a-descriptions-item>
						<a-descriptions-item label="品级">
							{{ selectedProduct.item_quality }}
						</a-descriptions-item>
						<a-descriptions-item label="属性">
							{{ selectedProduct.attributes }}
							<br />
						</a-descriptions-item>
						<a-descriptions-item label="简介">
							{{ selectedProduct.item_desc_cn }}
							<br />
						</a-descriptions-item>
					</a-descriptions>
					<a-empty
						v-else
						:image="simpleImage"
						description="请选择装备"
					/>
				</div>
			</a-flex>
		</a-card>
	</a-spin>
</template>

<script setup name="wiki-equipment">
import { computed, onBeforeMount, ref } from "vue";
import { getLumiMarketTvl, getLumiMarketProduct } from "/@/api";
import { isDev } from "/@/config";
// import { lumiProductInfo } from "@/data/lumiProductInfo";
import { lumiProductInfo } from "@/data/items";
import {
	equipmentTypes,
	careerOptions,
	essenceOptions,
	potionOptions,
} from "@/data/typeOptions";
import { blackList } from "@/data/productBlackList";
import { Empty } from "ant-design-vue";
const simpleImage = Empty.PRESENTED_IMAGE_SIMPLE;
const base = isDev ? "/" : "/Static/lumi/";
const maketPlaceProductData = ref([]);
const selectedCareer = ref("Warrior");
const selectedProduct = ref({});
const loading = ref(true);
const equipmentProducts = computed(() => {
	return maketPlaceProductData.value.filter((item) => {
		return (
			item.type === "装备" &&
			item.career === selectedCareer.value &&
			!blackList.includes(item.name)
		);
	});
});
const levels = computed(() => {
	return Array.from(
		new Set(maketPlaceProductData.value.map((i) => i.level))
	).sort((a, b) => a - b);
});
onBeforeMount(() => {
	getLumiMarketProduct()
		.then((res) => {
			if (res.state === "success") {
				maketPlaceProductData.value = res.data
					.map((item) => {
						const productInfo = lumiProductInfo.find(
							(i) => i.id === item.id
						);
						item.name_zh = productInfo?.name || "-";
						item.level = productInfo?.level || 99;
						item.job_type = productInfo?.job_type || "-";
						item.compose = productInfo?.compose || [];
						item.type = productInfo?.type || "-";
						item.equipment_type =
							productInfo?.equipment_type || "-";
						item.sort = 99;
						item.item_quality = productInfo?.item_quality || "-";
						item.item_desc_cn = productInfo?.item_desc_cn || "-";
						item.item_desc_eng = productInfo?.item_desc_eng || "-";
						item.use_desc_cn = productInfo?.use_desc_cn || "-";
						item.use_desc_eng = productInfo?.use_desc_eng || "-";
						item.from_desc_cn = productInfo?.from_desc_cn || "-";
						item.from_desc_eng = productInfo?.from_desc_eng || "-";
						item.attributes = productInfo?.attributes || "-";
						item.act = productInfo?.act || "-";
						item.luausd = productInfo?.luausd || "-";
						if (item.job_type === "战斗") item.career = "Warrior";
						if (item.job_type === "采集") item.career = "Collector";
						if (item.job_type === "种植") item.career = "Farmer";
						if (item.job_type === "无") item.career = "Fashion";
						if (item.equipment_type === "鞋") item.sort = 1;
						if (item.equipment_type === "腿") item.sort = 2;
						if (item.equipment_type === "胸") item.sort = 3;
						if (item.equipment_type === "手套") item.sort = 4;
						if (item.equipment_type === "帽子") item.sort = 5;
						if (item.equipment_type === "弓") item.sort = 6;
						if (item.equipment_type === "锤") item.sort = 7;
						if (item.equipment_type === "剑") item.sort = 8;
						if (item.equipment_type === "斧") item.sort = 9;
						if (item.equipment_type === "镰刀") item.sort = 10;
						if (item.equipment_type === "镐") item.sort = 11;
						if (item.equipment_type === "水壶") item.sort = 12;
						if (item.equipment_type === "刷子") item.sort = 13;
						if (item.equipment_type === "锄头") item.sort = 14;

						return item;
					})
					.sort((a, b) => a.sort - b.sort);
			}
			console.log(maketPlaceProductData.value);
			if (maketPlaceProductData.value.length > 0)
				selectedProduct.value = equipmentProducts.value[0];
		})
		.catch((err) => {
			console.log(err);
		})
		.finally(() => {
			loading.value = false;
		});
});

const selectE = (item) => {
	selectedProduct.value = item;
	//锚点定位到equipment-card
	const anchor = document.getElementById("equipment-card");
	anchor.scrollIntoView({ behavior: "smooth" });

};
const careerChange = () => {
	selectedProduct.value = equipmentProducts.value[0];
};
</script>
