<script setup name="wiki-equipment">
import { computed, onBeforeMount, ref, watch } from "vue";
import { getLumiMarketProduct } from "/@/api";
import { isDev,showBeta } from "/@/config";
import {
	equipmentTypes,
	careerOptions,
	essenceOptions,
	potionOptions,
} from "@/data/typeOptions";
import { blackList } from "@/data/productBlackList";
import { Empty } from "ant-design-vue";
const simpleImage = Empty.PRESENTED_IMAGE_SIMPLE;
const base = isDev ? "/" : "/Static/lumi/";
const props = defineProps({
	maketPlaceProductData: Array,
	loading: Boolean,
});
const selectedCareer = ref("战斗");
const selectedQuality = ref("Basic白(1)");
const selectedProduct = ref({});
const equipmentProducts = computed(() => {
	return props.maketPlaceProductData.filter((item) => {
		return (
			(item.type === "装备" || item.name.includes('装备碎片'))&&
			item.job_type === selectedCareer.value &&
			!blackList.find((i) => item.id === i.id)
		);
	}).sort((a, b) => a.level - b.level);
});
const levels = computed(() => {
	const tmp = Array.from(
		new Set(props.maketPlaceProductData.map((i) => i.level))
	).sort((a, b) => a - b)
	tmp.push(100)
	return tmp;
});
onBeforeMount(() => {
	if (props.maketPlaceProductData.length > 0)
		selectedProduct.value = equipmentProducts.value[0];
});
watch(
	() => props.maketPlaceProductData,
	(val) => {
		if (val.length > 0) selectedProduct.value = equipmentProducts.value[0];
	}
);
const selectE = (item) => {
	selectedProduct.value = item;
	selectedQuality.value = item.item_quality.split(',')[0];
	//锚点定位到equipment-card
	// const anchor = document.getElementById("equipment-card");
	// anchor.scrollIntoView({ behavior: "smooth" });
};
const careerChange = () => {
	selectedProduct.value = equipmentProducts.value[0];
	selectedQuality.value = equipmentProducts.value[0].item_quality.split(',')[0];
};
const returnQualityClass = (item) => {
	if(item.includes("Basic")) return "basic"
	if(item.includes("Enhanced")) return "enhanced"
	if(item.includes("Advanced")) return "advanced"
	if(item.includes("Super")) return "super"
	if(item.includes("Ultimate")) return "ultimate"

	return 'default'
};

const getImageUrl = (id) => {
	let a = props.maketPlaceProductData.find((j) =>j.id ===id)
	return `https://icons.lumiterra.net/item-icon-${a['id-int']}.png`;
};
</script>

