<script setup name="wiki-essence&potion">
import { computed, onBeforeMount, ref, watch } from "vue";
import { getLumiMarketTvl, getLumiMarketProduct } from "/@/api";
import { isDev } from "/@/config";
// import { lumiProductInfo } from "@/data/lumiProductInfo";
import { lumiProductInfo } from "@/data/items";
import {
	equipmentTypes,
	careerOptions,
	essenceOptions,
	potionOptions,
} from "@/data/typeOptions";
import { blackList } from "@/data/productBlackList";
import { Empty } from "ant-design-vue";
const simpleImage = Empty.PRESENTED_IMAGE_SIMPLE;
const base = isDev ? "/" : "/Static/lumi/";
const props = defineProps({
	maketPlaceProductData: Array,
	loading: Boolean,
});
// const maketPlaceProductData = ref([]);
// const selectedEssenceType = ref("Combat");

const essenceProducts = computed(() => {
	const a = [];
	const b = [];
	const c = [];
	const d = [];
	props.maketPlaceProductData.forEach((item) => {
		if (item.name.includes( "精华" ) && !blackList.includes(item.name_en)) {
			if (item.name_en.includes("Combat")) a.push(item);
			if (item.name_en.includes("Gather")) b.push(item);
			if (item.name_en.includes("Planting")) c.push(item);
			if (item.name_en.includes("Livestock")) d.push(item);
		}
	});
	return a.concat(b).concat(c).concat(d);
});
const potionProducts = computed(() => {
	const a = [];
	const b = [];
	const c = [];
	const d = [];
	const e = [];
	const f = [];
	const g = [];
	const h = [];
	props.maketPlaceProductData.forEach((item) => {
		if (
			item.name_en.includes("Potion") &&
			!blackList.includes(item.name_en)
		) {
			if (item.name_en.includes("Energy")) a.push(item);
			else if (item.name_en.includes("Attack")) b.push(item);
			else if (item.name_en.includes("Critical")) c.push(item);
			else if (
				item.name_en.includes("Healing") ||
				item.name_en.includes("Treatment")
			)
				d.push(item);
			else if (
				item.name_en.includes("Defense") ||
				item.name_en.includes("Defensive")
			)
				e.push(item);
			else if (
				item.name_en.includes("Speed") ||
				item.name_en.includes("Swiftness")
			)
				f.push(item);
			else if (item.name_en.includes("Life")) g.push(item);
			else h.push(item);
		}
	});
	return a
		.concat(b)
		.concat(c)
		.concat(d)
		.concat(e)
		.concat(f)
		.concat(g)
		.concat(h);
});
const essenceLevels = computed(() => {
	return Array.from(new Set(essenceProducts.value.map((i) => i.level))).sort(
		(a, b) => a - b
	);
});
const potionLevels = computed(() => {
	return Array.from(
		new Set(props.maketPlaceProductData.map((i) => i.level))
	).sort((a, b) => a - b);
});

const selectedProduct = ref({});

onBeforeMount(() => {
	if (props.maketPlaceProductData.length > 0)
		selectedProduct.value = essenceProducts.value[0];
});
watch(
	() => props.maketPlaceProductData,
	(val) => {
		if (val.length > 0) selectedProduct.value = essenceProducts.value[0];
	}
);
const selectE = (item) => {
	selectedProduct.value = item;
	//跳转至锚点
	// const anchor = document.getElementById("essence-card");
	// anchor.scrollIntoView({ behavior: "smooth" });
};
const essenceChange = () => {
	selectedProduct.value = essenceProducts.value[0];
};
</script>
