<script setup name="wiki-essence&potion">
import { computed, onBeforeMount, ref } from "vue";
import { getLumiMarketTvl, getLumiMarketProduct } from "/@/api";
import { isDev } from "/@/config";
// import { lumiProductInfo } from "@/data/lumiProductInfo";
import { lumiProductInfo } from "@/data/items";
import {
	equipmentTypes,
	careerOptions,
	essenceOptions,
	potionOptions,
} from "@/data/typeOptions";
import { blackList } from "@/data/productBlackList";
import { Empty } from "ant-design-vue";
const simpleImage = Empty.PRESENTED_IMAGE_SIMPLE;
const base = isDev ? "/" : "/Static/lumi/";
const maketPlaceProductData = ref([]);
// const selectedEssenceType = ref("Combat");

const loading = ref(true);
const essenceProducts = computed(() => {
	const a = [];
	const b = [];
	const c = [];
	const d = [];
	maketPlaceProductData.value.forEach((item) => {
		if (item.type === "精华" && !blackList.includes(item.name)) {
			if (item.name.includes("Combat")) a.push(item);
			if (item.name.includes("Gather")) b.push(item);
			if (item.name.includes("Planting")) c.push(item);
			if (item.name.includes("Livestock")) d.push(item);
		}
	});
	return a.concat(b).concat(c).concat(d);
});
const potionProducts = computed(() => {
	const a = [];
	const b = [];
	const c = [];
	const d = [];
	const e = [];
	const f = [];
	const g = [];
	const h = [];
	maketPlaceProductData.value.forEach((item) => {
		if (item.name.includes("Potion") && !blackList.includes(item.name)) {
			if (item.name.includes("Energy")) a.push(item);
			else if (item.name.includes("Attack")) b.push(item);
			else if (item.name.includes("Critical")) c.push(item);
			else if (
				item.name.includes("Healing") ||
				item.name.includes("Treatment")
			)
				d.push(item);
			else if (
				item.name.includes("Defense") ||
				item.name.includes("Defensive")
			)
				e.push(item);
			else if (
				item.name.includes("Speed") ||
				item.name.includes("Swiftness")
			)
				f.push(item);
			else if (item.name.includes("Life")) g.push(item);
			else h.push(item);
		}
	});
	return a
		.concat(b)
		.concat(c)
		.concat(d)
		.concat(e)
		.concat(f)
		.concat(g)
		.concat(h);
});
const essenceLevels = computed(() => {
	return Array.from(new Set(essenceProducts.value.map((i) => i.level))).sort(
		(a, b) => a - b
	);
});
const potionLevels = computed(() => {
	return Array.from(
		new Set(maketPlaceProductData.value.map((i) => i.level))
	).sort((a, b) => a - b);
});

const selectedProduct = ref({});

onBeforeMount(() => {
	getLumiMarketProduct()
		.then((res) => {
			if (res.state === "success") {
				maketPlaceProductData.value = res.data.map((item) => {
					const productInfo = lumiProductInfo.find(
						(i) => i.id === item.id
					);
					item.name_zh = productInfo?.name || "-";
					item.level = productInfo?.level || 99;
					item.job_type = productInfo?.job_type || "-";
					item.compose = productInfo?.compose || [];
					item.type = productInfo?.type || "-";
					item.item_quality = productInfo?.item_quality || "-";
					item.item_desc_cn = productInfo?.item_desc_cn || "-";
					item.item_desc_eng = productInfo?.item_desc_eng || "-";
					item.use_desc_cn = productInfo?.use_desc_cn || "-";
					item.use_desc_eng = productInfo?.use_desc_eng || "-";
					item.from_desc_cn = productInfo?.from_desc_cn || "-";
					item.from_desc_eng = productInfo?.from_desc_eng || "-";
					item.act = productInfo?.act || "-";
					item.luausd = productInfo?.luausd || "-";
					if (item.job_type === "战斗") item.career = "Warrior";
					if (item.job_type === "采集") item.career = "Collector";
					if (item.job_type === "种植") item.career = "Farmer";
					if (item.job_type === "无") item.career = "Fashion";
					return item;
				});
			}
			if (maketPlaceProductData.value.length > 0)
				selectedProduct.value = essenceProducts.value[0];
		})
		.catch((err) => {
			console.log(err);
		})
		.finally(() => {
			loading.value = false;
		});
});

const selectE = (item) => {
	selectedProduct.value = item;
	//跳转至锚点
	const anchor = document.getElementById("essence-card");
	anchor.scrollIntoView({ behavior: "smooth" });
};
const essenceChange = () => {
	selectedProduct.value = essenceProducts.value[0];
};
</script>
